```cpp
class A {
public:
  void f_pub() ;
protected:
  void f_prot();
};

class B : protected A {
public:
  void g() ;
};

class C: public B {
public:
  void h();
};

void A::f_pub() {
  A aa; B ba; C ca;
  aa.f_prot(); // (1)
  ba.f_prot(); // (2)
  ca.f_prot(); // (3)
}

void A::f_prot() {};

void B::g() {
  A ab; B bb; C cb;
  ab.f_prot(); // (4)
  bb.f_prot(); // (5)
  cb.f_prot(); // (6)
};

void C::h() {
  A ac; B bc; C cc;
  ac.f_prot(); // (7)
  bc.f_prot(); // (8)
  cc.f_prot(); // (9)
}
```
