```cpp
class Base {
public:
  virtual void show() { cout << "Base"; }
};

class Derived : public Base {
public:
  void show() { cout << "Derived"; }
};

class Wrapper {
public:
  Wrapper(Base& b) : ref(b) {}
  void callShow() { ref.show(); }
  void changeObject(Base& newRef) {
    ref = newRef;
  }
private:
  Base& ref;
};

int main(){
  Derived d;
  Base b;
  Wrapper wrapper(d);
  wrapper.callShow();
  wrapper.changeObject(b);
  wrapper.callShow();
  return EXIT_SUCCESS;
}
```
